import React from "react";
import { Card } from "react-bootstrap";
import nproducts from "./nproducts";

export const Novicedetails = ({ nproduct }) => {
  return (
    <div>
      <Card className="my-3 p-3 rounded">
        <a href={`/nproduct/${nproduct.id}`}>
          <Card.Img src={nproduct.image} variant="top" />
        </a>
        <Card.Body>
          <a href={`/nproducts/${nproduct.id}`}>
            <Card.Body.Title as="div">
              <strong>{nproduct.name}</strong>
            </Card.Body.Title>
          </a>
          <Card.Text as="div">
            <div className="my-3">
              {nproduct.rating} from {nproduct.numReviews}
            </div>
          </Card.Text>
          <Card.Text as="div"> $ {nproduct.price} </Card.Text>
        </Card.Body>
      </Card>
    </div>
  );
};
