import React from "react";
// import axios from "axios";
import nproducts from "./nproducts";
import {
  Row,
  Col,
  Button,
  ListGroup,
  Image,
  ListGroupItem,
} from "react-bootstrap";
import { Link } from "react-router-dom";

const Novicepdetails = () => {
  

  return (
    <div>
      <Link to="/novice" className="btn btn-light">
        {" "}
        Go Back
      </Link>
      <Row>
        <Col md={6}>
          <Image src={nproducts.image} alt={nproducts.name} fluid />
        </Col>
        <Col md={3}>
          <ListGroup variant="flush">
            <ListGroupItem>
              <h3>{nproducts.name}</h3>
            </ListGroupItem>
            <ListGroupItem>Price : ${nproducts.price}</ListGroupItem>
            <ListGroupItem> {nproducts.description} </ListGroupItem>
          </ListGroup>
        </Col>
        <Col md={3}>
          <ListGroupItem>
            <Row>
              <Col> Status: </Col>
              <Col>
                {nproducts.countInStock > 0 ? "In Stock!" : "Out of Stock"}
              </Col>
            </Row>
          </ListGroupItem>
          <ListGroupItem>
            <Button className="btn-block" type="button">
              {" "}
              Add to cart{" "}
            </Button>
          </ListGroupItem>
        </Col>
      </Row>
    </div>
  );
};

export default Novicepdetails;
