import React from "react";
import { nproducts } from "./nproducts";
import { Row, Col, ListGroup, Image, ListGroupItem } from "react-bootstrap";
import { useParams } from "react-router-dom";

const { id } = useParams;
const Novicepdetails = ({ match }) => {
  const nproduct = nproducts.find((p) => p.id === match.params.id);

  return (
    <div>
      <Row>
        <Col md={6}>
          <Image src={nproduct.image} alt={nproduct.name} fluid />
        </Col>
        <Col md={3}>
          <ListGroup variant="flush">
            <ListGroupItem>
              <h3>{nproduct.name}</h3>
            </ListGroupItem>
          </ListGroup>
        </Col>
      </Row>
    </div>
  );
};

export default Novicepdetails;
