import React, { useState, useEffect } from "react";
import axios from "axios";
import Novicedetails from "./Novicedetails";
import { Row, Col } from "react-bootstrap";
import Navbar from "./Navbar";
import {useCookies} from 'react-cookie';
var CryptoJS = require('crypto-js');

const Cart = () => {
  const [nproducts, setnproducts] = useState([]);
  useEffect(() => {

    const [cookies] = useCookies('user');
    var bytes = CryptoJS.AES.decrypt(cookies.user, 'my-secret-key@123');
    var decryptedData = JSON.parse(bytes.toString(CryptoJS.enc.Utf8));
    const user = decryptedData.email;

    const fetchProducts = async () => {
      const res = await fetch ("http://localhost:8080/cart", {
        method:"POST",
        headers:{
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          user
        })
      })
        
      const d = await res.json()

      const data = d.cart

      setnproducts(data);
    };
    fetchProducts();
  });;

  if (nproducts) { 
    return (
        <>
          <Navbar />
          <Row>
            {nproducts.map((nproduct) => (
              <Col key={nproduct._id} md={3}>
                <Novicedetails nproduct={nproduct} />
              </Col>
            ))}
          </Row>
        </>
      );
    };
  }
  

  

export default Cart;
